import React from 'react';
import './radio.scss';

type Props = React.HTMLProps<HTMLInputElement> & {
  radioData?: string[] | number[];
  extValue?: string | number;
};

export const Radio: React.FC<Props> = ({
  className,
  radioData,
  extValue,
  ...restProps
}) => {
  return radioData ? (
    <div className={className}>
      {radioData.map((value: string | number) => {
        return (
          <div key={value} className={'radio-control'}>
            <label htmlFor={String(value)}> {value}</label>
            <input
              {...restProps}
              type={'radio'}
              checked={extValue == value}
              id={String(value)}
              value={value}
            />
          </div>
        );
      })}
    </div>
  ) : null;
};
import React from 'react';

export const Textarea: React.FC<React.HTMLProps<HTMLTextAreaElement>> = (
  props,
) => {
  return <textarea {...props} />;
};
import React, {useState, useCallback, useMemo} from 'react';
import {ControlJsonHandler} from '@types';
import Textarea from '../textarea';
import './json-editor.scss';

interface Props {
  className?: string;
  propValue: string;
  propName: string;
  propUpdate: ControlJsonHandler;
}

export const JsonEditor: React.FC<Props> = ({
  className,
  propName,
  propValue,
  propUpdate,
}) => {
  const [editedJsonValue, setEditedJsonValue] = useState<string>();

  const textareaOnChange = useCallback(
    (event: React.ChangeEvent<HTMLTextAreaElement>) => {
      const {
        currentTarget: {value},
      } = event;

      setEditedJsonValue(value);
    },
    [],
  );

  const isEnabledToApply = useMemo(() => {
    return editedJsonValue == null;
  }, [editedJsonValue]);

  const onPropUpdate = useCallback(() => {
    propUpdate(propName, editedJsonValue!);
    setEditedJsonValue(undefined);
  }, [propName, editedJsonValue]);

  return (
    <div className={className + ' json-editor'}>
      <Textarea
        className={'json-editor-textarea'}
        onChange={textareaOnChange}
        id={propName}
        value={editedJsonValue ?? propValue}
      />
      <button
        className={'json-editor-apply-button'}
        disabled={isEnabledToApply}
        onClick={onPropUpdate}
      >
        Apply changes
      </button>
    </div>
  );
};
