export function escapeRegExp(value: string): string {
  return value.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
}
import {useState, useCallback, useEffect} from 'react';
import {escapeRegExp} from './utils';

export const PALETTE_PATH = '/REACT_BUDDY_PALETTE';
export const URL_CHANGE_EVENT = 'urlchange';

export type RouteProps = {
  path: string;
  exact?: boolean;
  children: JSX.Element;
};

export const useRoute = (path: string, exact?: boolean) => {
  const [, setUpdate] = useState<boolean>(false);

  const update = useCallback(() => {
    setUpdate((reRender) => {
      return !reRender;
    });
  }, []);

  useEffect(() => {
    window.addEventListener(URL_CHANGE_EVENT, update);
    window.addEventListener('popstate', update);

    return () => {
      window.removeEventListener(URL_CHANGE_EVENT, update);
      window.removeEventListener('popstate', update);
    };
  }, []);

  return isMatchPath(path, exact);
};

export function isMatchPath(path: string, exact: boolean = false): boolean {
  const currentPath = getCurrentPath();
  const match = new RegExp(transformPath(path)).exec(currentPath);

  if (match) {
    const [url] = match;
    return exact ? currentPath === url : true;
  }

  return false;
}

function transformPath(path: string): string {
  return escapeRegExp(encodeURI(path));
}

function getCurrentPath() {
  const {hash, pathname} = window.location;
  return hash ? hash.replace('#', '') : pathname;
}

export function historyPush(url: string) {
  window.history.replaceState({}, '', url);
  dispatchUrlChangeEvent(url);
}

function dispatchUrlChangeEvent(url: string) {
  const urlChangeEvent = new CustomEvent(URL_CHANGE_EVENT, {
    bubbles: true,
    detail: url,
  });
  window.dispatchEvent(urlChangeEvent);
}
import React, {useState, useMemo, useEffect} from 'react';
import ToolsPanel from './tools-panel';
import {ToolsPropsModifier} from '@types';
import {useRoute, PALETTE_PATH, historyPush} from '../routing/routing';
import styles from './previews.module.scss';

interface Props {
  palette?: JSX.Element | null;
  children?: JSX.Element | JSX.Element[];
}

export const Previews: React.FC<Props> = ({
  children,
  palette = null,
}: Props) => {
  const [toolsPropsToEdit, setToolsPropsToEdit] =
    useState<ToolsPropsModifier>(null);
  const [toolsPanelEnabled, enableToolsPanel] = useState<boolean>(
    () => window.__PROPERTIES_EDIT_PANEL_ENABLED__ ?? false,
  );
  const isPalettePath = useRoute(PALETTE_PATH);

  const childrenWithSetProps = useMemo(() => {
    return children
      ? React.Children.map(children, (child: JSX.Element) => {
          return React.cloneElement(child, {setToolsPropsToEdit});
        })
      : null;
  }, [children]);

  useEffect(() => {
    window.enableComponentsPropsPanelEditor = (toolsPanelStatus: boolean) => {
      window.setPropertiesEditPanelStatus?.(toolsPanelStatus);
      enableToolsPanel(toolsPanelStatus);
    };

    window.reactBuddyHistoryPush = historyPush;
    window.cefQuery?.({request: 'event:react-toolbox-initialized'});
  }, []);

  if (isPalettePath) {
    return palette;
  }

  return (
    <div className={styles.previewsMain}>
      <div className={styles.previewsContent}>{childrenWithSetProps}</div>
      {toolsPanelEnabled && (
        <div className={styles.previewsToolsPanel}>
          <ToolsPanel toolsPropsToEdit={toolsPropsToEdit} />
        </div>
      )}
    </div>
  );
};
import React, {useEffect, useState} from 'react';
import {ComponentPreviewProps, PropsModifier} from '@types';
import {RouteProps, useRoute} from '../routing/routing';

type RoutePreviewProps = RouteProps & {
  setPropsToEdit: (propsModifier: PropsModifier) => void;
};

export const RoutePreview: React.FC<RoutePreviewProps> = ({
  children,
  path,
  exact,
  setPropsToEdit,
}) => {
  const [modfiedProps, setModifiedProps] =
    useState<ComponentPreviewProps>(null);

  const isMatchPath = useRoute(path, exact);

  useEffect(() => {
    if (isMatchPath) {
      const currentProps = modfiedProps ?? children.props;
      const propsToEdit = {
        props: currentProps,
        updateProps: setModifiedProps,
      };
      setPropsToEdit(propsToEdit);
    }

    return () => {
      isMatchPath ? null : setModifiedProps(null);
    };
  }, [isMatchPath]);

  const childrenWithUpdatedProps = modfiedProps
    ? React.cloneElement(children, {...modfiedProps})
    : children;

  return isMatchPath ? <>{childrenWithUpdatedProps}</> : null;
};
import React from 'react';
import styles from './react-buddy-error-boundary.module.scss';

interface Props {
  componentName?: string;
}

interface State {
  error: Error | null;
}
export class ReactBuddyErrorBoundary extends React.Component<Props, State> {
  state: State = {error: null};

  componentDidCatch(error: Error) {
    console.error(error);
    this.setState({error});
  }

  componentDidUpdate(_prevProps: Props, prevState: State) {
    if (prevState.error != null) {
      this.setState({error: null});
    }
  }

  render() {
    const {error} = this.state;
    const {componentName, children} = this.props;

    if (error != null) {
      return (
        <div className={styles.errorMessageContainer}>
          <h2 className={styles.errorMessageTitle}>
            {componentName
              ? `Something went wrong while rendering ${componentName} component`
              : 'Something went wrong'}
            </h2>
          <p>{error.message}</p>
          <pre className={styles.errorMessageStack}>{error.stack}</pre>
        </div>
      );
    }

    return children;
  }
}
import React, {useCallback} from 'react';
import {RoutePreview} from '../routing/route-preview';
import {PropsModifier, ToolsPropsModifier, PropsEditInfo} from '@types';
